import 'module-alias/register'; 
import { exec } from 'child_process';
import * as Server from '@/server';
import * as Utils from '@/utils'; 
import XConfig from '@config/_core'
import fileManipulator from '@/lib/files/file-manipulator';

const TestUtils = require('@test/utils');

const buildServer = () => {
    Server.build().then(app => {
    app.listen(XConfig.get('/application/port'), '0.0.0.0')
        .then(_ => {
        const exitHandler = Utils.terminate(app, {
            coredump: false,
            timeout: 500
        })

        console.log(`Server started. ENV=${process.env.NODE_ENV}, PORT=${XConfig.get('/application/port')}`);
        console.log();
        startTesting()

        process.on('uncaughtException', exitHandler(1, 'Unexpected Error'))
        process.on('unhandledRejection', exitHandler(1, 'Unhandled Promise'))
        process.on('SIGTERM', exitHandler(0, 'SIGTERM'))
        process.on('SIGINT', exitHandler(0, 'SIGINT'))
        })

        .catch(err => {
        console.log('Error starting server: ', err)
        process.exit(1)
        })
    })
    .catch(err => console.log(err))
}

const startTesting = () => {
    console.log("=================================> BEGIN <==================================");
    console.log(`APP Name: ${XConfig.get('/application/info/title')}`);
    console.log(`APP Version: ${XConfig.get('/application/info/version')}`);

        before((done) => {
            console.log('--------------------------- START: Prepare Server -------------------------- ');
            console.log('Open server connection...');
             setTimeout(function () {
                TestUtils.migrateDB();
                setTimeout(function () {
                    console.log('--------------------------- FINISH: Prepare Server ------------------------- ');
                    console.log();
                    console.log('------------------------------- START: Test -------------------------------- ');
                    done();
                 }, 16000)
             }, 2000)
        });



        after((done) => {
            console.log('------------------------------- FINISH: Test -------------------------------- ');
            console.log();
            console.log('--------------------------- START: Clean Up Server -------------------------- ');
            TestUtils.cleanDB();
            setTimeout(function () {
                const statifFile = XConfig.get('/application/staticFile');
                const dir = fileManipulator.getDirPath({
                    definition: statifFile.definition.default,
                    manifest: 'root'
                });
                exec(`rm -rf ${statifFile.root}${dir}`);
                console.log('Close server connection...');
                console.log('--------------------------- FINISH: Clean Up Server ------------------------- ');
                console.log();
                console.log('==================================> END <==================================== ');
                done();
             }, 5000)
        }); 


        //API V1
        describe('API v1',  () => {
            require('./testcases/v1/_base');
        })
}

buildServer();

import fastify from 'fastify';
import os from 'os';
import pino from 'pino';
import { fromEnv } from '@/utils';
import regex from '@helpers/string/regex';
import { NOT_FOUND, INTERNAL_SERVER_ERROR } from '@rpc/error';


//IMPORT MODULES
import FsCors from 'fastify-cors';
import FsHelmet from 'fastify-helmet';
import FsSwagger from 'fastify-swagger';
import FsStatic from 'fastify-static';
import XConfig from '@config/_core';
import XModAuthJWTVerify from '@authentication/jwt/jwtAuthVerify';
import XModDBPostgres from '@/lib/connection/database/postgres';

// LOG
const logsConfig = {
  formatters: {
    level (level) {
      return { level }
    }
  },
  timestamp: () => `,"time":"${new Date(Date.now()).toISOString()}"`,
  level: fromEnv('LOG_LEVEL')
}

const logger = {
  development: {
    prettyPrint: {
      colorize: true,
      levelFirst: true,
      ignore: 'time,pid,hostname'
    },
    level: fromEnv('LOG_LEVEL')
  },
  staging: logsConfig,
  production: logsConfig
}


// Schema Compiler
const Ajv = require('ajv');
const ajv = new Ajv({
  $data: true,
  strict: true,
  allErrors: true,
  useDefaults: true,
  coerceTypes: true,
  validateFormats: true,
  unicodeRegExp: true,
  formats: {
    'uri': regex.uri
  }
});

ajv.addKeyword({
  keyword: "isFileType",
  compile: (schema, parent, it) => {
    parent.type = 'file'
    delete parent.isFileType
    return () => true
  }
}).addKeyword({keyword: 'example'});

// Build Server
const build = async () => {
  // Fastify Server
  let server;
  if(fromEnv('NODE_ENV')!='test'){
    server = fastify({
      bodyLimit: 1048576 * 2,
      logger: pino(logger[fromEnv('NODE_ENV')]),
      http2: true,
      https: XConfig.get('/application/tlsOptions'),
      schemaController: {
        compilersFactory: {
          buildValidator: ajv
        }
      }
    });
  }else{
    server = fastify({
      bodyLimit: 1048576 * 2,
      logger: null,
      schemaController: {
        compilersFactory: {
          buildValidator: ajv
        }
      }
    });
  }

  console.table(os.cpus());

  // Register Modules
  await server
  //CORS
  .register(FsCors, {
    origin: '*',
    methods: ['GET','POST','PATCH','UPDATE']
  })
  //Helmet
  .register(FsHelmet, {
    contentSecurityPolicy: {
      directives: {
        baseUri: ['\'self\''],
        defaultSrc: ['\'self\''],
        scriptSrc: ['\'self\''],
        objectSrc: ['\'self\''],
        workerSrc: ['\'self\'', 'blob:'],
        frameSrc: ['\'self\''],
        formAction: ['\'self\''],
        upgradeInsecureRequests: []
      }
    }
  })
  // FILE-UPLOAD
  .register(FsStatic, {
    root: `${XConfig.get('/application/staticFile/root')}/${XConfig.get('/application/staticFile/definition/default/directory')}`,
    prefix: `/${XConfig.get('/application/staticFile/definition/default/prefix')}/`
  })
  .register(require('fastify-multipart'),
  {
    // attachFieldsToBody: true
    addToBody: true
  })
  // AUTHENTICATION
  .register(XModAuthJWTVerify, {
    name: 'authenticate-jwt-default',
    config: XConfig.get('/jwtAuthOptions/default'),
    validationMethod: 'none'
  })
  // DATABASE
  .register(XModDBPostgres,
  [
    {
      credential: XConfig.get('/databases/postgres/default'),
      info: {
        id: "DEFAULT",
        env: XConfig.get('/application/environment')
      }
    }
  ])
  // DOCUMENTATION
  .register(FsSwagger, XConfig.get('/swagger'))
  .setValidatorCompiler(({ schema, method, url, httpPart }) =>
    ajv.compile(schema)
  );

  //ROUTES
  await server
  .register(require('@api/v1/_core/routes'), { prefix: 'api/v1' })
  .setNotFoundHandler((request, reply) => {
    server.log.debug(`Route not found: ${request.method}:${request.raw.url}`)

    reply.status(404).send({
      statusCode: 404,
      error: NOT_FOUND,
      message: `Route ${request.method}:${request.raw.url} not found`
    })
  })
  .setErrorHandler((err, request, reply) => {
    server.log.debug(`Request url: ${request.raw.url}`)
    server.log.debug(`Payload: ${request.body}`)
    server.log.error(`Error occurred: ${err}`)

    const code = err.statusCode ?? 400

    reply.status(code).send({
      statusCode: code,
      error: err.name ?? "Bad Request",
      message: err.message ?? err
    })
  });

  return server
}

// implement inversion of control to make the code testable
export {
  build
}

import fromEnv from './_fromenv';
import terminate from './_terminate';

export  {
  fromEnv,
  terminate
}

const terminate = (server, options = { coredump: false, timeout: 500 }) => {
  const exit = code => {
    options.coredump ? process.abort() : process.exit(code)
  }

  return (code, reason) => async (err, promise) => {
    if (err && err instanceof Error) {
      console.log(err.message, err.stack)
    }

    // server.mongo.connection.close() 
    server.close(exit)
    setTimeout(exit, options.timeout).unref()
  }
}

export default terminate

