import FastifyPlugin from 'fastify-plugin';
import ResponseRPC from '@rpc/response';

async function validateUser(validateMethod: string){
  switch(validateMethod){
    case 'none':
      return true;
    default:
      return false;
  }
}

type jwtAuthVerify = {
  name: string;
  config: any;
  validationMethod: string;
};

export default FastifyPlugin(async function(fastify, opts: jwtAuthVerify) {
  const customMessages = {
    badRequestErrorMessage: 'Format is Authorization: Bearer [token]',
    noAuthorizationInHeaderMessage: 'Autorization header is missing!',
    authorizationTokenExpiredMessage: 'Authorization token expired',
    // for the below message you can pass a sync function that must return a string as shown or a string
    authorizationTokenInvalid: (err) => {
      return `Authorization token is invalid: ${err.message}`
    }
  }

  fastify.register(require("fastify-jwt"), {
    secret: opts.config.key,
    verify: {
      allowedIss: opts.config.verify.iss,
      allowedAud: opts.config.verify.aud
    },
    messages: customMessages
  })

  fastify.decorate(opts.name, async function(request, reply, done) {
    try {
      const decodedToken = await request.jwtVerify();
      if(await validateUser(opts.validationMethod)){
        request.auth = {
          userdata : decodedToken.userdata,
          isAuthenticated: true
        };
      }else{
        request.auth = {
          isAuthenticated: false
        };
        ResponseRPC.unauthorized(reply)
        done()
      }

    } catch (err) {
      reply.send(err)
      done()
    }
  });
})

'use strict';

export default {
  sendResponseHTTP: function(reply, code: number, message?: string, data?: any) {
    const response_success = [200,201];
    const response_failed = [400,404,409,401,500];
    let response = {}

    if(!(response_success.includes(code) || response_failed.includes(code))){
      code = 400;
    }

    if(response_success.includes(code)){
      if(message==null){
        message="success";
      }
      response={ statusCode: code,
                 message: message,
                 data: data
               }
    }

    if(response_failed.includes(code)){
      if(message==null){
        message="failed";
      }
      response={ statusCode: code,
                 message: message
               }
    }

    reply.code(code).send(response);
  },
  success: function(reply, message?: string, data?: any) {
    const code = 200
    let response = {}
    if(message==null){
      message="Success";
    }
    response={ 
      statusCode: code,
      message: message,
      data: data
    }
    reply.code(code).send(response);
  },
  created: function(reply, message?: string, data?: any) {
    const code = 201
    let response = {}
    if(message==null){
      message="Created";
    }
    response={ 
      statusCode: code,
      message: message,
      data: data
    }
    reply.code(code).send(response);
  },
  badRequest: function(reply, message?: string, data?: any) {
    const code = 400
    let response = {}
    if(message==null){
      message="Bad Request";
    }
    response={ 
      statusCode: code,
      message: message,
      error: data
    }
    reply.code(code).send(response);
  },
  unauthorized: function(reply, message?: string, data?: any) {
    const code = 401
    let response = {}
    if(message==null){
      message="Unauthorized";
    }
    response={ 
      statusCode: code,
      message: message
    }
    reply.code(code).send(response);
  },
  notFound: function(reply, message?: string, data?: any) {
    const code = 404
    let response = {}
    if(message==null){
      message="Not found.";
    }
    response={ 
      statusCode: code,
      message: message
    }
    reply.code(code).send(response).hijack();
  }
}

'use strict';
const Sequelize = require('sequelize');
import FastifyPlugin from 'fastify-plugin';


var connection={};
function plugin(fastify, opts, done) {
  opts.forEach(options=> {
    const credential=options.credential;
    const node = `[${options.info.id} - ${options.info.env}]`;

    const node_connection = new Sequelize(`postgres://${encodeURIComponent(credential.username)}:${encodeURIComponent(credential.password)}@${credential.host}:${credential.port}/${encodeURIComponent(credential.database)}`, 
    {
      logging: false,
      pool: {
        max: 5,
        min: 0,
        acquire: 30000,
        idle: 10000
      }
    });
    connection[options.info.id]=node_connection;
    try {
      node_connection.authenticate().then(() => {
        console.log(` [*] Connection to the POSTGRESQL:${node} database has been established successfully.`);
       })
       .catch(err => {
        console.log(` [*] ERROR - Unable to connect to the POSTGRESQL:${node} database:`, err)
      });
    } catch (error) {
      console.log(` [*] Unable to connect to the POSTGRESQL:${node} database:`, error);
    }
  });
  done();
}

export default FastifyPlugin(plugin, {
  name: 'postgres',
});

