        }else{
          ResponseHelper.sendResponseHTTP(reply, 500);
        }
      }else{
        ResponseHelper.sendResponseHTTP(reply, 401, "auth data not found");
      }
    }else{
      ResponseHelper.sendResponseHTTP(reply, 400);
    }
  } catch (err) {
    throw Boom.boomify(err)
  }
};

